'use strict';

var React = require('react-native');
// var Featured = require('./Featured');
var Search = require('./Search');
var Photo = require('./Photo');
// var Photo = require('./ReactProject');

var {
    AppRegistry,
    TabBarIOS,
    Component
   } = React;

var base64Icon = 'data:image/png;base64,';


class Dufine extends Component {

    constructor(props) {
        super(props);
        this.state = {
            avatarSource: null,
            selectedTab: 'search'

        };
    }

    render() {
        return (
            <TabBarIOS selectedTab={this.state.selectedTab}>

                <TabBarIOS.Item
                    selected={this.state.selectedTab === 'search'}
                    title="Add Dufinition"
                    icon={{uri: base64Icon, scale: 3}}
                    onPress={() => {
                        this.setState({
                            selectedTab: 'search'
                        });
                    }}>
                    <Search/>
                </TabBarIOS.Item>
                <TabBarIOS.Item
                    selected={this.state.selectedTab === 'photo'}
                    title="View Saved Dufinition"
                    icon={{uri: base64Icon, scale: 3}}
                    onPress={() => {
                        this.setState({
                            selectedTab: 'photo'
                        });
                    }}>
                    <Photo/>
                </TabBarIOS.Item>
            </TabBarIOS>
        );
    }
}


AppRegistry.registerComponent('Dufine', () => Dufine);